00199 str_t* newStringWithLength(str_t *str, const void *initValue, uint32_t initLength, char *buffer, uint32_t bufferLength) {
00200     if (str == NULL || initLength >= bufferLength)
00201         return NULL;
00202     str->value = buffer;
00203     str->length = initLength;
00204     str->capacity = bufferLength;
00205     memcpy(str->value, initValue, initLength);
00206     TERMINATE_STRING(str);
00207     return str;
00208 }
