00237 str_t* concatChar(str_t *str, char charToConcat) {
00238     if (str == NULL || str->length >= (str->capacity - 1))
00239         return NULL;
00240     *STRING_END(str) = charToConcat;
00241     str->length++;
00242     return str;
00243 }
