00245 str_t* stringFormat(str_t *str, const char *format, ...) {
00246     if (str == NULL || format == NULL)
00247         return NULL;
00248     clearString(str);
00249     va_list vaList;
00250     va_start(vaList, format);
00251 
00252     for (; str != NULL && *format != '\0'; format++) {
00253         if (*format != '%') {
00254             str = concatChar(str, *format);
00255             continue;
00256         }
00257 
00258         format++;   // skip also '%'
00259         uint8_t flags = 0;
00260         format += parseFormatFlags(format, &flags);
00261 
00262         int32_t widthField = NO_RESULT;
00263         format += parseFormatFieldWith(format, &vaList, &widthField, &flags);
00264 
00265         int32_t precisionField = NO_RESULT;
00266         format += parseFormatPrecision(format, &vaList, &precisionField);
00267 
00268         char lengthField[LENGTH_FIELD_MAX_SIZE] = { 0 };
00269         format += parseLengthField(lengthField, format);
00270 
00271         uint8_t base = DEC_BASE;    // default base
00272         switch (*format) {
00273             case 'c':
00274                 str = formatCharacter(str, flags, widthField - 1, &vaList);
00275                 continue;
00276             case 's':
00277                 str = formatChars(str, flags, widthField, precisionField, &vaList);
00278                 continue;
00279             case 'S':
00280                 str = formatString(str, flags, widthField, precisionField, &vaList);
00281                 continue;
00282             case 'p':
00283                 str = formatPointer(str, flags, widthField, precisionField, (uintptr_t) va_arg(vaList, void*));
00284                 continue;
00285             case 'n':   // Print nothing, but writes the number of characters written so far into an integer pointer parameter.
00286                 str = concatChar(str, '\n');    // BufferString holds string length, so no need to count this. Just add new line like in Java 
00287                 continue;
00288             case '%':
00289                 str = concatChar(str, '%');
00290                 continue;
00291 
00292             case 'o':
00293                 base = OCT_BASE;
00294                 break;
00295             case 'b':
00296                 base = BIN_BASE;
00297                 break;
00298             case 'x':
00299                 SET_FLAG(flags, LOWER_CASE_FLAG);
00300             case 'X':
00301                 base = HEX_BASE;
00302                 break;
00303 
00304             case 'd':
00305             case 'i':
00306                 SET_FLAG(flags, SIGNED_NUMBER_FLAG);
00307                 break;
00308             case 'u':
00309                 break;
00310 
00311             case 'I':
00312                 SET_FLAG(flags, SIGNED_NUMBER_FLAG);
00313                 format += IS_INT_8(lengthField) ? SKIP_ONE_CHAR : SKIP_TWO_CHARS;
00314                 break;
00315             case 'U':
00316                 format += IS_INT_8(lengthField) ? SKIP_ONE_CHAR : SKIP_TWO_CHARS;
00317                 break;
00318 
00319 #ifdef ENABLE_FLOAT_FORMATTING
00320             case 'f':
00321                 SET_FLAG(flags, LOWER_CASE_FLAG);
00322             case 'F':
00323                 str = formatFloat(str, va_arg(vaList, double), flags, widthField, precisionField);
00324                 continue;
00325 
00326             case 'e':
00327                 SET_FLAG(flags, LOWER_CASE_FLAG);
00328             case 'E':
00329                 str = formatExponential(str, va_arg(vaList, double), flags, widthField, precisionField);
00330                 continue;
00331 
00332             case 'g':
00333                 SET_FLAG(flags, LOWER_CASE_FLAG);
00334             case 'G':
00335                 SET_FLAG(flags, ADAPTIVE_EXPONENT_FLAG);
00336                 str = formatExponential(str, va_arg(vaList, double), flags, widthField, precisionField);
00337                 continue;
00338 #endif
00339 
00340             default:    // unknown char, just concatenate as is
00341                 str = concatChar(str, *format);
00342                 continue;
00343         }
00344 
00345         str = formatNumber(str, flags, lengthField, widthField, precisionField, base, &vaList);
00346     }
00347 
00348     va_end(vaList);
00349     return str;
00350 }
