00352 str_t* concatCharsByLength(str_t *str, const char *strToConcat, uint32_t length) {
00353     if (str == NULL || length >= (str->capacity - str->length))
00354         return NULL;
00355     memcpy(STRING_END(str), strToConcat, length);
00356     str->length += length;
00357     TERMINATE_STRING(str);
00358     return str;
00359 }
