00396 str_t* swapCase(str_t *str) {
00397     for (uint32_t i = 0; i < str->length; i++) {
00398         char valueChar = str->value[i];
00399         str->value[i] = (char) (islower(valueChar) ? toupper(valueChar) : tolower(valueChar));
00400     }
00401     return str;
