00404 str_t* replaceFirstOccurrence(str_t *source, const char *target, const char *replacement) {
00405     char *sourcePointer = strstr(source->value, target);
00406     if (sourcePointer == NULL)
00407         return NULL;
00408 
00409     uint32_t targetLength = strlen(target);
00410     uint32_t replacementLength = strlen(replacement);
00411     uint32_t tailLength = strlen(sourcePointer + targetLength) + 1;
00412 
00413     if (targetLength >= source->capacity)
00414         return NULL;
00415     memmove(sourcePointer + replacementLength, sourcePointer + targetLength, tailLength);
00416     strncpy(sourcePointer, replacement, replacementLength);
00417     source->length = strlen(source->value);
00418     return source;
00419 }
00420 
00421 str_t* replaceAllOccurrences(str_t *source, const char *target, const char *replacement) {
00422     while (replaceFirstOccurrence(source, target, replacement) != NULL)
00423         ;
00424     return source;
00425 }
