00427 str_t* trimAll(str_t *str) {
00428     if (str == NULL)
00429         return NULL;
00430 
00431     while (isspace((unsigned char ) *str->value)) {    // Trim leading space
00432         str->value++;
00433         str->length--;
00434     }
00435 
00436     if (*str->value == 0) { // All spaces?
00437         return str;
00438     }
00439 
00440     // Trim trailing space
00441     char *stringEnd = str->value + str->length - 1;
00442     while (stringEnd > str->value && isspace((unsigned char ) *stringEnd)) {
00443         stringEnd--;
00444     }
00445 
00446     // Write new null terminator character
00447     stringEnd[1] = '\0';
00448     str->length = strlen(str->value);
00449     return str;
00450 }
00451 
00452 str_t* reverseString(str_t *str) {
00453     for (uint32_t i = 0; i < (str->length / 2); i++) {
00454         char headChar = str->value[i];
00455         char tailChar = str->value[str->length - i - 1];
00456         str->value[i] = tailChar;
00457         str->value[str->length - i - 1] = headChar;
00458     }
00459     return str;
00460 }
