00462 str_t* substringFromTo(str_t *source, str_t *destination, uint32_t beginIndex, uint32_t endIndex) {
00463     bool isStringNotInBounds = (beginIndex > endIndex || endIndex > source->length);
00464     if (isStringNotInBounds)
00465         return NULL;
00466     uint32_t subLen = (endIndex - beginIndex);
00467     memmove(destination->value, source->value + beginIndex, subLen);
00468     destination->length = subLen;
00469     TERMINATE_STRING(destination);
00470     return destination;
00471 }
00472 
00473 str_t* substringFrom(str_t *source, str_t *destination, uint32_t beginIndex) {
00474     return substringFromTo(source, destination, beginIndex, source->length);
00475 }
