00477 str_t* substringAfter(str_t *source, str_t *destination, const char *separator) {
00478     char *substringPointer = strstr(source->value, separator);
00479     if (substringPointer == NULL)
00480         return source;
00481     uint32_t separatorLength = strlen(separator);
00482     substringPointer += separatorLength;
00483     return copyStringByLength(destination, substringPointer, strlen(substringPointer));
00484 }
00485 
00486 int32_t lastIndexOfString(str_t *str, const char *stringToFind) {
00487     if (str == NULL || stringToFind == NULL)
00488         return NO_RESULT;
00489     uint32_t substringLength = strlen(stringToFind);
00490 
00491     uint32_t i = str->length;
00492     while (true) {
00493         uint32_t step = (i > substringLength) ? substringLength : 1;
00494         if (i == 0) {
00495             break;
00496         }
00497         i -= step;
00498 
00499         char *substringPointer = strstr(str->value + i, stringToFind);
00500         if (substringPointer != NULL) {
00501             return (substringPointer - str->value);
00502         }
00503     }
00504     return NO_RESULT;
00505 }
00506 
00507 str_t* substringAfterLast(str_t *source, str_t *destination, const char *separator) {
00508     int32_t position = lastIndexOfString(source, separator);
00509     if (position == NO_RESULT) {
00510         return source;
00511     }
00512     uint32_t separatorLength = strlen(separator);
00513     char *substringPointer = source->value + position + separatorLength;    // move to the last occurrence
00514     return copyStringByLength(destination, substringPointer, strlen(substringPointer));
00515 }
00516 
00517 int32_t indexOfString(str_t *str, const char *stringToFind, uint32_t fromIndex) {
00518     if (str == NULL || stringToFind == NULL || fromIndex >= str->length)
00519         return NO_RESULT;
00520     char *strPointer = strstr(str->value + fromIndex, stringToFind);
00521     return strPointer != NULL ? (strPointer - str->value) : NO_RESULT;
00522 }
00523 
00524 str_t* substringBefore(str_t *source, str_t *destination, const char *separator) {
00525     int32_t position = indexOfString(source, separator, 0);
00526     if (position == NO_RESULT) {
00527         return source;
00528     }
00529     char *substringPointer = source->value;
00530     return copyStringByLength(destination, substringPointer, position);
00531 }
00532 
00533 str_t* substringBeforeLast(str_t *source, str_t *destination, const char *separator) {
00534     int32_t position = lastIndexOfString(source, separator);
00535     if (position == NO_RESULT) {
00536         return source;
00537     }
00538     char *substringPointer = source->value;
00539     return copyStringByLength(destination, substringPointer, position);
00540 }
00541 
00542 str_t* substringBetween(str_t *source, str_t *destination, const char *open, const char *close) {
00543     if (source == NULL || destination == NULL)
00544         return NULL;
00545     char *startPointer = strstr(source->value, open);
00546     if (startPointer != NULL) {  // check that substring start is found
00547         startPointer += strlen(open);
00548         char *endPointer = strstr(startPointer, close);
00549         size_t substringLength = endPointer != NULL ? endPointer - startPointer : 0;
00550         if (substringLength > 0 && substringLength < destination->capacity) {  // check that substring end is found and dest have enough capacity
00551             strncat(destination->value, startPointer, substringLength);
00552             destination->length = substringLength;
00553             return destination;
00554         }
00555         return NULL;
00556     }
00557     return NULL;
00558 }
00559 
00560 str_iter_t getStringSplitIterator(str_t *str, const char *delimiter) {
00561     str_iter_t iterator = { .str = str, .delimiter = delimiter, .delimiterLength = delimiter != NULL ? strlen(delimiter) : 0, .nextToken = str->value };
00562     return iterator;
00563 }
00564 
00565 bool hasNextSplitToken(str_iter_t *iterator, str_t *token) {
00566     if (iterator == NULL || iterator->str == NULL || token == NULL)
00567         return false;
00568     char *startPointer = iterator->nextToken;
00569     if (startPointer == NULL) {
00570         return false;
00571     }
00572 
00573     char *endPointer = strstr(startPointer, iterator->delimiter);
00574     if (endPointer == NULL) {   // check that delimiter exist
00575         if (iterator->nextToken != NULL && iterator->nextToken != iterator->str->value) { // copy last part only when source string have at least one existing delimiter
00576             copyStringByLength(token, startPointer, strlen(iterator->nextToken));
00577             iterator->nextToken = NULL;
00578             return true;
00579         }
00580         return false;
00581     }
00582 
00583     uint32_t tokenLength = endPointer - startPointer;
00584     copyStringByLength(token, startPointer, tokenLength);
00585     startPointer += tokenLength;
00586     iterator->nextToken = startPointer + iterator->delimiterLength;
00587     return true;
00588 }
00589 
00590 str_t* joinChars(str_t *str, const char *delimiter, uint32_t argCount, ...) {
00591     va_list valist;
00592     va_start(valist, argCount);
00593     str_t backupString = { 0 };
00594     shallowStringCopy(str, &backupString);
00595     uint32_t delimiterLength = strlen(delimiter);
00596 
00597     bool isFailedToJoin = false;
00598     for (uint32_t i = 0; i < argCount; i++) {
00599         char *argValue = va_arg(valist, char*);
00600         if (concatChars(str, argValue) == NULL) {
00601             isFailedToJoin = true;
00602             break;
00603         }
00604         if (i != argCount - 1) {
00605             if (concatCharsByLength(str, delimiter, delimiterLength) == NULL) {
00606                 isFailedToJoin = true;
00607                 break;
00608             }
00609         }
00610     }
00611 
00612     if (isFailedToJoin) {
00613         shallowStringCopy(&backupString, str);
00614         TERMINATE_STRING(str);  // restore previous string ending by length
00615         va_end(valist);
00616         return NULL;
00617     }
00618     va_end(valist);
00619     return str;
00620 }
00621 
00622 str_t* joinStringArray(str_t *str, const char *delimiter, uint32_t argCount, char **tokens) {
00623     str_t backupString = { 0 };
00624     shallowStringCopy(str, &backupString);
00625     uint32_t delimiterLength = strlen(delimiter);
00626 
00627     bool isFailedToJoin = false;
00628     for (uint32_t i = 0; i < argCount; i++) {
00629         char *argValue = tokens[i];
00630         if (concatChars(str, argValue) == NULL) {
00631             isFailedToJoin = true;
00632             break;
00633         }
00634         if (i != argCount - 1) {
00635             if (concatCharsByLength(str, delimiter, delimiterLength) == NULL) {
00636                 isFailedToJoin = true;
00637                 break;
00638             }
00639         }
00640     }
00641 
00642     if (isFailedToJoin) {
00643         shallowStringCopy(&backupString, str);
00644         TERMINATE_STRING(str);  // restore previous string ending by length
00645         return NULL;
00646     }
00647     return str;
00648 }
00649 
00650 str_t* joinStrings(str_t *str, const char *delimiter, uint32_t argCount, ...) {
00651     va_list valist;
00652     va_start(valist, argCount);
00653     str_t backupString = { 0 };
00654     shallowStringCopy(str, &backupString);
