00656 
00657     bool isFailedToJoin = false;
00658     for (uint32_t i = 0; i < argCount; i++) {
00659         str_t *argValue = va_arg(valist, str_t*);
00660         if (concatString(str, argValue) == NULL) {
00661             isFailedToJoin = true;
00662             break;
00663         }
00664         if (i != argCount - 1) {
00665             if (concatCharsByLength(str, delimiter, delimiterLength) == NULL) {
00666                 isFailedToJoin = true;
00667                 break;
00668             }
00669         }
00670     }
00671 
00672     if (isFailedToJoin) {
00673         shallowStringCopy(&backupString, str);
00674         TERMINATE_STRING(str);  // restore previous string ending by length
00675         va_end(valist);
00676         return NULL;
00677     }
00678     va_end(valist);
