00683     while (str != NULL && count > 0) {
00684         str = concatChar(str, repeatChar);
00685         count--;
00686     }
00687     return str;
00688 }
00689 
00690 str_t* repeatChars(str_t *str, const char *repeatChars, uint32_t count) {
00691     while (str != NULL && count > 0) {
00692         str = concatChars(str, repeatChars);
00693         count--;
00694     }
