00698 str_t* int64ToString(str_t *str, int64_t value) {
00699     char *bufferPointer = str->value;
00700     uint64_t convertedValue = (value < 0) ? -value : value;
00701     int32_t length = numberToStringByBase(convertedValue, bufferPointer, DEC_BASE, 0);
00702 
00703     if (value < 0) {
00704         if ((length + 1) >= str->capacity)
00705             return NULL;
00706         bufferPointer[length] = '-';
00707         length++;   // add minus sign to length
00708     }
00709 
00710     str->length = length;
00711     return reverseString(str);
00712 }
00713 
00714 str_t* uInt64ToString(str_t *str, uint64_t value) {
00715     char *bufferPointer = str->value;
00716     int32_t length = numberToStringByBase(value, bufferPointer, DEC_BASE, 0);
00717     str->length = length;
00718     return reverseString(str);
00719 }
