00721 static inline bool isBuffStringEmpty(str_t *str) {
00722     return (str == NULL || str->value[0] == '\0');
00723 }
00724 
00725 bool isBuffStringNotEmpty(str_t *str) {
00726     return !isBuffStringEmpty(str);
00727 }
00728 
00729 bool isBuffStringBlank(str_t *str) {
00730     while (isBuffStringNotEmpty(str)) {
00731         if (!isspace(*str->value)) {
00732             return false;
00733         }
00734         str->value++;
00735     }
00736     return true;
00737 }
00738 
00739 bool isBuffStringEquals(str_t *one, str_t *two) {
00740     if (one == two)
00741         return true;
00742 
00743     if (one != NULL && two != NULL) {
00744         size_t oneLength = one->length;
00745         if (oneLength == two->length) {
00746             return strncmp(one->value, two->value, oneLength) == 0;
00747         }
00748     }
00749     return false;
00750 }
00751 
00752 bool isBuffStringEqualsIgnoreCase(str_t *one, str_t *two) {
00753     if (one == two)
00754         return true;
00755 
00756     if (one != NULL && two != NULL) {
00757         size_t oneLength = one->length;
00758         if (oneLength == two->length) {
00759             return strncasecmp(one->value, two->value, oneLength) == 0;
00760         }
00761     }
00762     return false;
00763 }
00764 
00765 int32_t indexOfChar(str_t *str, char charToFind, uint32_t fromIndex) {
00766     if (str == NULL || fromIndex >= str->length)
00767         return NO_RESULT;
00768     for (int32_t i = (int32_t) fromIndex; i < str->length; i++) {
00769         if (str->value[i] == charToFind) {
00770             return i;
00771         }
00772     }
00773     return NO_RESULT;
00774 }
00775 
00776 bool isStringStartsWith(str_t *str, const char *prefix, uint32_t toOffset) {
00777     if (str == NULL || prefix == NULL)
00778         return false;
00779     uint32_t prefixLength = strlen(prefix);
00780     if (toOffset > (str->length - prefixLength))
00781         return false;
00782 
00783     const char *valuePointer = str->value + toOffset;
00784     for (uint32_t i = 0; i < prefixLength; i++) {
00785         if (valuePointer[i] != prefix[i]) {
00786             return false;
00787         }
00788     }
00789     return true;
00790 }
00791 
00792 bool isStringStartsWithIgnoreCase(str_t *str, const char *prefix, uint32_t toOffset) {
00793     if (str == NULL || prefix == NULL)
00794         return false;
00795     uint32_t prefixLength = strlen(prefix);
00796     if (toOffset > (str->length - prefixLength))
00797         return false;
00798 
00799     const char *valuePointer = str->value + toOffset;
00800     for (uint32_t i = 0; i < prefixLength; i++) {
00801         if (tolower(valuePointer[i]) != tolower(prefix[i])) {
00802             return false;
00803         }
00804     }
00805     return true;
00806 }
00807 
00808 static void shallowStringCopy(str_t *source, str_t *destination) {
00809     destination->capacity = source->capacity;
00810     destination->length = source->length;
00811     destination->value = source->value;
00812 }
00813 
00814 static uint8_t parseFormatFlags(const char *format, uint8_t *flags) {
00815     uint8_t flagsLength = 0;
00816     bool haveNextFlag = true;
00817     while (haveNextFlag) {
00818         char formatChar = *format;
00819 
00820         switch (formatChar) {
00821             case '-':
00822                 SET_FLAG(*flags, LEFT_ALIGN_FLAG);
00823                 break;
00824             case '+':
00825                 SET_FLAG(*flags, PLUS_FLAG);
00826                 break;
00827             case ' ':
00828                 SET_FLAG(*flags, SPACE_FLAG);
00829                 break;
00830             case '0':
00831                 SET_FLAG(*flags, ZEROES_PADDING_FLAG);
00832                 break;
00833             case '#':
00834                 SET_FLAG(*flags, SPECIAL_FLAG);
00835                 break;
00836             default:
00837                 haveNextFlag = false;
00838                 break;
00839         }
00840 
00841         if (haveNextFlag) {
00842             format++;
00843             flagsLength++;
00844         }
00845     }
00846     return flagsLength;
00847 }
00848 
00849 static uint8_t parseFormatFieldWith(const char *format, va_list *vaList, int32_t *widthField, uint8_t *flags) {
00850     if (isdigit(*format)) {
00851         *widthField = 0;
00852         return stringToNumber(format, widthField);
00853 
00854     } else if (*format == '*') {
00855         *widthField = va_arg(*vaList, int32_t);   // dynamic width field value must be provided
00856         if (*widthField < 0) {
00857             *widthField = -*widthField;
00858             SET_FLAG(*flags, LEFT_ALIGN_FLAG);
00859         }
00860         return SKIP_ONE_CHAR; // skip '*'
00861     }
00862     return 0;
00863 }
00864 
00865 static uint8_t parseFormatPrecision(const char *format, va_list *vaList, int32_t *precision) {
00866     if (*format == '.') {
00867         format++;   // skip '.'
00868         if (isdigit(*format)) {
00869             *precision = 0;
00870             return stringToNumber(format, precision) + SKIP_ONE_CHAR;   // also skip '.'
00871 
00872         } else if (*format == '*') {
00873             *precision = va_arg(*vaList, int32_t);   // dynamic precision field value must be provided
00874             return SKIP_TWO_CHARS; // skip '*' and '.'
00875         }
00876 
00877         if (*precision < 0) {
00878             *precision = 0;
00879         }
00880         return SKIP_ONE_CHAR;      // also skip '.'
00881     }
00882     return 0;
00883 }
00884 
00885 static uint8_t parseLengthField(char *lengthField, const char *format) {
00886     if (*format == 'h' || *format == 'l' || *format == 'L') {
00887         lengthField[0] = *format;
00888         format++;   // skip length field
00889         if (*format == 'h' || *format == 'l') {
00890             lengthField[1] = *format;
00891             return SKIP_TWO_CHARS;   // skip additional field
00892         }
00893         return SKIP_ONE_CHAR;
00894 
00895     } else if (*format == 'I' || *format == 'U') {  // custom fields as U8, I8, U16, I16, U32, I32, U64, I64
00896         format++;   // skip type
00897         if (*format == '8') {
00898             lengthField[0] = *format;  // byte
00899             return 0;
00900         }
00901         lengthField[0] = *format++;   // all other int types. Do not return any length, because designator reversed instead standard types
00902         lengthField[1] = *format;
00903     }
00904     return 0;
00905 }
00906 
00907 static str_t* formatCharacter(str_t *str, uint8_t flags, int32_t widthField, va_list *vaList) {
00908     if (IS_FLAG_NOT_SET(flags, LEFT_ALIGN_FLAG)) {
00909         while (widthField > 0) {
00910             str = concatChar(str, ' ');
00911             widthField--;
00912         }
00913     }
00914 
00915     char valueChar = (char) va_arg(*vaList, int);
00916     str = concatChar(str, valueChar);
00917     if (IS_FLAG_SET(flags, LEFT_ALIGN_FLAG)) {
00918         while (widthField > 0) {
00919             str = concatChar(str, ' ');
00920             widthField--;
00921         }
00922     }
00923     return str;
00924 }
00925 
00926 static str_t* formatChars(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, va_list *vaList) {
00927     char *valueStr = va_arg(*vaList, char*);
00928     if (valueStr == NULL)
00929         return NULL;
00930     uint32_t maxLength = (precision < 0 || precision > str->capacity) ? (str->capacity - str->length) : precision;
00931     uint32_t length = strnlen(valueStr, maxLength);
00932     widthField = (widthField > 0) ? widthField : 0;
00933     return doFormatChars(str, valueStr, length, flags, widthField);
00934 }
00935 
00936 static str_t* formatString(str_t *str, uint8_t flags, int32_t widthField, int64_t precision, va_list *vaList) {
00937     str_t *valueStr = va_arg(*vaList, str_t*);
00938     if (valueStr == NULL || valueStr->length > (str->capacity - str->length))
00939         return NULL;
00940     uint32_t maxLength = (precision < 0 || precision > str->capacity) ? valueStr->length + 1 : precision;
00941     uint32_t length = strnlen(valueStr->value, maxLength);
00942     widthField = (widthField > 0) ? widthField : 0;
00943     return doFormatChars(str, valueStr->value, length, flags, widthField);
00944 }
00945 
00946 static str_t* doFormatChars(str_t *str, const char *valueStr, uint32_t length, uint8_t flags, int32_t widthField) {
00947     if (IS_FLAG_NOT_SET(flags, LEFT_ALIGN_FLAG)) {
00948         while (length < widthField) {
00949             str = concatChar(str, ' ');
00950             widthField--;
00951         }
00952     }
00953 
00954     str = concatCharsByLength(str, valueStr, length);
00955     while (length < widthField) {
00956         str = concatChar(str, ' ');
00957         widthField--;
00958     }
00959     return str;
00960 }
00961 
00962 static str_t* formatPointer(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint64_t pointerAddress) {
00963     if (widthField <= 0) {
00964         widthField = POINTER_DEFAULT_WIDTH;
00965         SET_FLAG(flags, ZEROES_PADDING_FLAG);
00966     }
00967     return numberToString(str, pointerAddress, NO_SIGN, HEX_BASE, widthField, precision, flags);
00968 }
00969 
00970 static str_t* formatNumber(str_t *str, uint8_t flags, const char *lengthField, int32_t widthField, int32_t precision, uint8_t base,
00971         va_list *vaList) {
00972     if (lengthField[0] == 'h' || IS_INT_8(lengthField) || IS_INT_16(lengthField)) {    //   Expect int-sized integer argument which was promoted from a short.
00973         if (lengthField[1] == 'h' || IS_INT_8(lengthField)) {    // Expect int-sized integer argument which was promoted from a char.
00974             return formatByte(str, flags, widthField, precision, base, vaList);
00975         }
00976         return formatShort(str, flags, widthField, precision, base, vaList);
00977     }
00978 
00979     if (lengthField[0] == 'l' || IS_INT_64(lengthField)) {
00980         if (lengthField[1] == 'l' || IS_INT_64(lengthField)) {
00981             return formatLongLong(str, flags, widthField, precision, base, vaList);
00982         }
00983         return formatLong(str, flags, widthField, precision, base, vaList);
00984     }
00985 
00986     return formatInt(str, flags, widthField, precision, base, vaList);
00987 }
00988 
00989 #ifdef ENABLE_FLOAT_FORMATTING
00990 static str_t* formatFloat(str_t *str, double decimalValue, uint8_t flags, int32_t widthField, int32_t precision) {
00991     static const double FLOAT_POW_OF_10[] = { 1, 10, 100, 1000, 10000, 100000, 1000000, 10000000, 100000000, 1000000000 };
00992 
00993     if (isNanOrInfinity(str, decimalValue, widthField, flags)) {
00994         return str;
00995     }
00996 
00997     if ((decimalValue > FORMAT_MAX_FLOAT_VALUE) || (decimalValue < -FORMAT_MAX_FLOAT_VALUE)) {
00998         return formatExponential(str, decimalValue, flags, widthField, precision);
00999     }
01000 
01001     if (precision < 0) {
01002         precision = FORMAT_DEFAULT_FLOAT_PRECISION;
01003     }
01004 
01005     int32_t bufferLength = 0;
01006     char tmpDecimalBuffer[FORMAT_FLOAT_BUFFER_SIZE] = { 0 };
01007     while (bufferLength < FORMAT_FLOAT_BUFFER_SIZE && precision > FORMAT_MAX_FLOAT_PRECISION) { // limit precision to 9, cause a precision > 9 can lead to overflow errors
01008         tmpDecimalBuffer[bufferLength] = '0';
01009         bufferLength++;
01010         precision--;
01011     }
01012 
01013     double power = FLOAT_POW_OF_10[precision];
01014     decimalValue *= power;
01015     decimalValue = (decimalValue >= 0) ? ((int64_t) (decimalValue + 0.5)) / power : ((int64_t) (decimalValue - 0.5)) / power;    // round by precision
01016 
01017     int64_t wholePart = (int64_t) decimalValue;
01018     char sign = resolveSign(&wholePart, flags, &widthField);
01019     if (precision == 0) {
01020         return numberToString(str, wholePart, sign, DEC_BASE, widthField, 0, flags);  // return string if only whole part is needed
01021     }
01022 
01023     decimalValue = (sign == '-') ? -decimalValue : decimalValue;
01024     double tmpFractional = (decimalValue - (int32_t) wholePart) * power;  // convert fraction part as a whole
01025     tmpFractional = (((int32_t) (tmpFractional + 0.5)) / power) * power; // round conversion
01026     int32_t fractionalPart = (int32_t) tmpFractional;   // extract fraction
01027 
01028     int32_t decimalLength = numberToStringByBase(fractionalPart, (tmpDecimalBuffer + bufferLength), DEC_BASE, flags);
01029     bufferLength += decimalLength;
01030     int32_t precisionDigitCount = (precision - bufferLength);
01031 
01032     while (precisionDigitCount > 0 && bufferLength < FORMAT_FLOAT_BUFFER_SIZE) {   // add extra precision 0s
01033         tmpDecimalBuffer[bufferLength] = '0';
01034         precisionDigitCount--;
01035         bufferLength++;
01036     }
01037 
01038     if (bufferLength < FORMAT_FLOAT_BUFFER_SIZE) {  // add decimal point
01039         tmpDecimalBuffer[bufferLength] = '.';
01040         bufferLength++;
01041     }
01042 
01043     uint32_t startValueLength = str->length;
01044     int32_t wholeLength = IS_FLAG_SET(flags, LEFT_ALIGN_FLAG) ? 0 : (widthField - bufferLength);   // when '-' flag set, concat only number without padding
01045     str = numberToString(str, wholePart, sign, DEC_BASE, wholeLength, 1, flags); // concat whole part with padding minus length of decimal part
01046 
01047     while (str != NULL && bufferLength > 0) { // concat fraction and decimal point part
01048         char digitChar = tmpDecimalBuffer[--bufferLength];
01049         str = concatChar(str, digitChar);
01050     }
01051 
01052     if (IS_FLAG_SET(flags, LEFT_ALIGN_FLAG)) {
01053         uint32_t endValueLength = str->length - startValueLength;
01054         uint32_t paddingLength = (widthField >= endValueLength) ? widthField - endValueLength : 0;
01055         repeatChar(str, ' ', paddingLength);
01056     }
01057     return str;
01058 }
01059 
01060 static str_t* formatExponential(str_t *str, double decimalValue, uint8_t flags, int32_t widthField, int32_t precision) {
01061     if (isNanOrInfinity(str, decimalValue, widthField, flags)) {
01062         return str;
01063     }
01064 
01065     if (precision < 0) {
01066         precision = FORMAT_DEFAULT_FLOAT_PRECISION;
01067     }
01068 
01069     bool isNegative = decimalValue < 0;
01070     decimalValue = isNegative ? -decimalValue : decimalValue;
01071 
01072     // Determine the decimal exponent. Based on the algorithm by David Gay (https://github.com/jwiegley/gdtoa/blob/master/dtoa.c)
01073     double_cast_t converter = { .decimal = decimalValue };
01074     int32_t exponentOfTwo = (converter.parts.exponent - DOUBLE_EXPONENT_ZERO_VALUE);
01075     converter.parts.exponent = DOUBLE_EXPONENT_ZERO_VALUE;  // drop the exponent so converter.decimal is now in [1,2)
01076 
01077     // now approximate log10 from the log2 integer part and an expansion of ln around 1.5
01078     int32_t exponentValue = (int32_t) ((converter.decimal - 1.5) * LOG_APPROXIMATION + LOG10_OF_1_5 + exponentOfTwo * LOG10_OF_2);
01079     exponentOfTwo = (int32_t) (exponentValue * LOG2_OF_10 + 0.5);  // now we want to compute 10^exponentValue, but we want to be sure it won't overflow
01080 
01081     double zValue = (exponentValue * NATURAL_LOG_OF_10) - (exponentOfTwo * NATURAL_LOG_OF_2);
01082     double zPowOfTwo = zValue * zValue;
01083 
01084     converter.parts.exponent += exponentOfTwo;    // return exp value
01085     converter.parts.mantissa = 0;   // drop mantissa
01086 
01087     // compute exp(z) using continued fractions, see https://en.wikipedia.org/wiki/Exponential_function#Continued_fractions_for_ex
01088     converter.decimal = converter.decimal * (1 + 2 * zValue / (2 - zValue + (zPowOfTwo / (6 + (zPowOfTwo / (10 + zPowOfTwo / 14))))));
01089 
01090     if (decimalValue < converter.decimal) {    // correct for rounding errors
01091         exponentValue--;
01092         converter.decimal /= 10;
01093     }
01094 
01095     // the exponent format is "%+03d" and largest value is "307", so set aside 4 characters
01096     int32_t exponentMinWidth = EXPONENT_WIDTH_DEFAULT_VALUE;
01097     if (IS_FLAG_SET(flags, ADAPTIVE_EXPONENT_FLAG)) {   // in "%g" mode, "precision" is the number of "significant figures" not decimals
01098         // do we want to fall back to "%f" mode?
01099         if (decimalValue >= 1e-4 && decimalValue < 1e6) {
01100             precision = (precision > exponentValue) ? (precision - exponentValue - 1) : 0;
01101             // no characters in exponent
01102             exponentValue = 0;
01103             exponentMinWidth = 0;
01104 
01105         } else {
01106             precision--;
01107         }
01108     }
01109 
01110     // check that everything fits
01111     int32_t decimalWith = (widthField > exponentMinWidth) ? (widthField - exponentMinWidth) : 0;
01112     bool isLeftPadFlagSet = IS_FLAG_SET(flags, LEFT_ALIGN_FLAG);
01113     if (isLeftPadFlagSet) {
01114         BIT_CLEAR(flags, LEFT_ALIGN_FLAG);    // if we're padding on the right, DON'T pad the floating part
01115         decimalWith = 0;
01116     }
01117 
01118     decimalValue = (exponentValue != 0) ? (decimalValue / converter.decimal) : decimalValue; // rescale the float value
01119     decimalValue = isNegative ? -decimalValue : decimalValue;
01120     decimalWith = (decimalWith > 0 && exponentMinWidth > 0) ? (decimalWith - 1) : decimalWith;
01121 
01122     uint32_t startValueLength = str->length;
01123     str = formatFloat(str, decimalValue, flags, decimalWith, precision);        // output the floating part
01124 
01125     if (exponentMinWidth > 0) {     // output the exponent part
01126         str = concatChar(str, IS_FLAG_SET(flags, LOWER_CASE_FLAG) ? 'e' : 'E');
01127         char sign = (exponentValue < 0) ? '-' : '+';
01128         exponentValue = (exponentValue < 0) ? -exponentValue : exponentValue;
01129         SET_FLAG(flags, ZEROES_PADDING_FLAG);    // set zeroes to exponent value
01130         str = numberToString(str, exponentValue, sign, DEC_BASE, 0, exponentMinWidth - 1, flags);
01131     }
01132 
01133     if (isLeftPadFlagSet) {
01134         uint32_t endValueLength = str->length - startValueLength;
01135         uint32_t paddingLength = (widthField >= endValueLength) ? widthField - endValueLength : 0;
01136         repeatChar(str, ' ', paddingLength);
01137     }
01138     return str;
01139 }
01140 #endif
01141 
01142 static inline str_t* formatByte(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint8_t base, va_list *vaList) {
01143     if (IS_FLAG_SET(flags, SIGNED_NUMBER_FLAG)) {    // "hhi"
01144         signed char signedInt = (signed char) va_arg(*vaList, signed int);
01145         int64_t number = (int64_t) signedInt;
01146         char sign = resolveSign(&number, flags, &widthField);
01147         return numberToString(str, number, sign, base, widthField, precision, flags);
01148     }
01149     int64_t number = (unsigned char) va_arg(*vaList, signed int);   // "hhu"
01150     return numberToString(str, number, NO_SIGN, base, widthField, precision, flags);
01151 }
01152 
01153 static inline str_t* formatShort(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint8_t base, va_list *vaList) {
01154     if (IS_FLAG_SET(flags, SIGNED_NUMBER_FLAG)) {    // "hi"
01155         int64_t number = (short) va_arg(*vaList, signed int);
01156         char sign = resolveSign(&number, flags, &widthField);
01157         return numberToString(str, number, sign, base, widthField, precision, flags);
01158     }
01159     unsigned short number = (unsigned short) va_arg(*vaList, signed int);   // "hu"
01160     return numberToString(str, number, NO_SIGN, base, widthField, precision, flags);
01161 }
01162 
01163 static inline str_t* formatLongLong(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint8_t base, va_list *vaList) {
01164     if (IS_FLAG_SET(flags, SIGNED_NUMBER_FLAG)) {    // "lld/i"
01165         int64_t number = va_arg(*vaList, signed long long);
01166         char sign = resolveSign(&number, flags, &widthField);
01167         return numberToString(str, number, sign, base, widthField, precision, flags);
01168     }
01169     unsigned long long number = va_arg(*vaList, unsigned long long);    // "llu"
01170     return numberToString(str, number, NO_SIGN, base, widthField, precision, flags);
01171 }
01172 
01173 static inline str_t* formatLong(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint8_t base, va_list *vaList) {
01174     if (IS_FLAG_SET(flags, SIGNED_NUMBER_FLAG)) {    // "ld/i"
01175         int64_t number = va_arg(*vaList, signed long);
01176         char sign = resolveSign(&number, flags, &widthField);
01177         return numberToString(str, number, sign, base, widthField, precision, flags);
01178     }
01179     unsigned long number = va_arg(*vaList, unsigned long);  // "lu"
01180     return numberToString(str, number, NO_SIGN, base, widthField, precision, flags);
01181 }
01182 
01183 static inline str_t* formatInt(str_t *str, uint8_t flags, int32_t widthField, int32_t precision, uint8_t base, va_list *vaList) {
01184     if (IS_FLAG_SET(flags, SIGNED_NUMBER_FLAG)) {    // "d/i"
01185         int64_t number = va_arg(*vaList, signed int);
01186         char sign = resolveSign(&number, flags, &widthField);
01187         return numberToString(str, number, sign, base, widthField, precision, flags);
01188     }
01189     uint64_t number = va_arg(*vaList, unsigned int);    // "u"
01190     return numberToString(str, number, NO_SIGN, base, widthField, precision, flags);
01191 }
01192 
01193 static str_t* numberToString(str_t *str, uint64_t number, char sign, uint8_t base, int32_t size, int32_t precision, uint8_t flags) {
01194     if (number == 0) {
01195         BIT_CLEAR(flags, SPECIAL_FLAG);
01196         precision = (size < 0 && precision < 0) ? 1 : precision;    // when field width, precision not provided and number is 0, concat single '0' to string
01197 
01198         if (precision <= 0) {
01199             if (size >= 0) {
01200                 BIT_CLEAR(flags, ZEROES_PADDING_FLAG);  // concat only ' '
01201                 str = concatLeftPadding(str, &sign, base, &size, flags);
01202             }
01203             return concatSignIfPresent(str, sign);
01204         }
01205     }
01206 
01207     if (IS_FLAG_SET(flags, LEFT_ALIGN_FLAG)) {
01208         BIT_CLEAR(flags, ZEROES_PADDING_FLAG);
01209     }
01210 
01211     if (IS_FLAG_SET(flags, SPECIAL_FLAG)) {
01212         if (base == HEX_BASE || base == BIN_BASE) {
01213             size -= HEX_SIZE;
01214 
01215         } else if (base == OCT_BASE) {
01216             size--;
01217         }
01218     }
01219 
01220     char tmpNumberBuffer[FORMAT_NUMBER_BUFFER_SIZE] = { 0 };
01221     int32_t numberLength = numberToStringByBase(number, tmpNumberBuffer, base, flags);
01222     precision = (numberLength > precision) ? numberLength : precision;
01223 
01224     size -= precision;
01225     size = (size > 0) ? size : 0;
01226 
01227     str = concatLeftPadding(str, &sign, base, &size, flags);
01228     str = concatSignIfPresent(str, sign);
01229 
01230     while (numberLength < precision) {  // add precision before value
01231         str = concatChar(str, '0');
01232         precision--;
01233     }
01234 
01235     while (numberLength > 0) { // reverse concat number chars
01236         str = concatChar(str, tmpNumberBuffer[--numberLength]);
01237     }
01238 
01239     return concatRightPadding(str, size);
01240 }
01241 
01242 static uint8_t stringToNumber(const char *numberStr, int32_t *resultValue) {
01243     uint8_t numberLength = 0;
01244 
01245     while (isdigit(*numberStr) && numberLength <= UINT64_DIGITS_MAX_COUNT) {
01246         *resultValue = (*resultValue * 10) + (*numberStr - '0');
01247         numberLength++;
01248         numberStr++;
01249     }
01250     return numberLength;
01251 }
01252 
01253 static int32_t numberToStringByBase(uint64_t number, char *numberBuffer, uint8_t base, uint8_t flags) {
01254     /* called only with base 2, 8, 10 or 16, thus don't need more than "F..."  */
01255     static const char DIGITS[] = "0123456789ABCDEF";
01256 
01257     int32_t length = 0;
01258     bool isUpperCaseValue = IS_FLAG_SET(flags, LOWER_CASE_FLAG);
01259     do {
01260         uint64_t result = number % base;
01261         number = number / base;
01262         char digit = DIGITS[result];
01263         numberBuffer[length] = (char) (isUpperCaseValue ? tolower(digit) : digit);
01264         length++;
01265     } while (number > 0);
01266 
01267     return length;
01268 }
01269 
01270 static str_t* concatLeftPadding(str_t *str, char *sign, uint8_t base, int32_t *size, uint8_t flags) {
01271     if (IS_FLAG_NOT_SET(flags, LEFT_ALIGN_FLAG)) {
01272         char paddingChar = IS_FLAG_SET(flags, ZEROES_PADDING_FLAG) ? '0' : ' ';
01273         if (paddingChar == '0') {
01274             str = concatSignIfPresent(str, *sign);
01275             str = concatSpecialIfPresent(str, base, flags);
01276             str = repeatChar(str, paddingChar, *size);
01277 
01278         } else {
01279             str = repeatChar(str, paddingChar, *size);
01280             str = concatSignIfPresent(str, *sign);
01281             str = concatSpecialIfPresent(str, base, flags);
01282         }
01283         *size = 0;
01284         *sign = NO_SIGN;
01285     }
01286     return str;
01287 }
01288 
01289 static inline str_t* concatSpecialIfPresent(str_t *str, uint8_t base, uint8_t flags) {
01290     if (IS_FLAG_SET(flags, SPECIAL_FLAG)) {
01291         if (base == OCT_BASE) {
01292             str = concatChar(str, '0');
01293 
01294         } else if (base == HEX_BASE) {
01295             str = concatChar(str, '0');
01296             str = concatChar(str, IS_FLAG_SET(flags, LOWER_CASE_FLAG) ? 'x' : 'X');
01297 
01298         } else if (base == BIN_BASE) {
01299             str = concatCharsByLength(str, "0b", 2);
01300         }
01301     }
01302     return str;
01303 }
01304 
01305 static inline str_t* concatSignIfPresent(str_t *str, char sign) {
01306     return (sign != NO_SIGN) ? concatChar(str, sign) : str;
01307 }
01308 
01309 static inline str_t* concatRightPadding(str_t *str, int32_t size) {
01310     return (size > 0) ? repeatChar(str, ' ', size) : str;
01311 }
01312 
01313 static char resolveSign(int64_t *number, uint8_t flags, int32_t *widthField) {
01314     char sign = NO_SIGN;
01315     if (*number < 0) {
01316         sign = '-';
01317         *number = -*number;
01318         *widthField -= 1;
01319 
01320     } else if (IS_FLAG_SET(flags, PLUS_FLAG)) {
01321         sign = '+';
01322         *widthField -= 1;
01323 
01324     } else if (IS_FLAG_SET(flags, SPACE_FLAG)) {
01325         sign = ' ';
01326         *widthField -= 1;
01327     }
01328     return sign;
01329 }
01330 
01331 #ifdef ENABLE_FLOAT_FORMATTING
01332 static bool isNanOrInfinity(str_t *str, double decimalValue, int32_t widthField, uint8_t flags) {
01333     char sign = NO_SIGN;
01334     if (isnan(decimalValue)) {
01335         widthField -= NAN_LENGTH;
01336         concatLeftPadding(str, &sign, DEC_BASE, &widthField, flags);
01337         concatChars(str, IS_FLAG_SET(flags, LOWER_CASE_FLAG) ? "nan" : "NaN");
01338         concatRightPadding(str, widthField);
01339         return true;
01340 
01341     } else if (decimalValue < -DBL_MAX) {
01342         widthField -= INF_WITH_SIGN_LENGTH;
01343         concatLeftPadding(str, &sign, DEC_BASE, &widthField, flags);
01344         concatChars(str, IS_FLAG_SET(flags, LOWER_CASE_FLAG) ? "-inf" : "-INF");
01345         concatRightPadding(str, widthField);
01346         return true;
01347 
01348     } else if (decimalValue > DBL_MAX) {
01349         widthField = IS_FLAG_SET(flags, PLUS_FLAG) ? (widthField - INF_WITH_SIGN_LENGTH) : (widthField - INF_LENGTH);
01350         concatLeftPadding(str, &sign, DEC_BASE, &widthField, flags);
01351         if (IS_FLAG_SET(flags, PLUS_FLAG)) {
01352             concatChar(str, '+');
01353         }
01354         concatChars(str, IS_FLAG_SET(flags, LOWER_CASE_FLAG) ? "inf" : "INF");
01355         concatRightPadding(str, widthField);
01356         return true;
01357     }
01358     return false;
01359 }
01360 #endif
