01371 static inline bool isBuffStringNotBlank(str_t *str) {
01372     return !isBuffStringBlank(str);
01373 }
01374 
01375 static inline bool isBuffStringNotEquals(str_t *one, str_t *two) {
01376     return !isBuffStringEquals(one, two);
01377 }
