00202     iec_totype(q, IEC_T_BOOL);
00203 
00204     if (iec_get_value(r)) {
00205         iec_set_value(*cv, 0);
00206     } else if (iec_get_value(cu) && (iec_get_value(*cv) < iec_get_value(pv)))
00207         iec_set_value(*cv, iec_get_value(*cv) + 1);
00208 
00209     iec_set_value(*q, iec_get_value(*cv) >= iec_get_value(pv))
00210 
00211     return IEC_OK;
