00276 uint8_t iec_tp(iec_t *timer, iec_t in, iec_t pt, iec_t *et) {
00277     iec_type_allowed(*timer, IEC_T_TIMER);
00278 
00279     if (!iec_is_initialized(*timer))
00280         iec_initialize_timer(timer, pt);
00281 
00282     if (iec_get_value(in)) {
00283         if (iec_timer(*timer)->t0 == 0) {
00284             iec_timer(*timer)->t0 = hw_millis();
00285         }
00286     }
00287 
00288     if (iec_timer(*timer)->t0 > 0) {
00289         iec_timer(*timer)->et = hw_millis() - iec_timer(*timer)->t0;
00290         if (iec_timer(*timer)->et >= iec_timer(*timer)->pt) {
00291             iec_timer(*timer)->et = iec_timer(*timer)->pt;
00292         }
00293     }
00294 
00295     if (!iec_get_value(in) && iec_timer(*timer)->et == iec_timer(*timer)->pt) {
00296         iec_timer(*timer)->t0 = 0;
00297         iec_timer(*timer)->et = 0;
00298     }
00299 
00300     iec_timer(*timer)->q = iec_timer(*timer)->et > 0 && iec_timer(*timer)->et < iec_timer(*timer)->pt;
00301 
00302     if (*et != NULL) {
00303         iec_type_allowed(*et, IEC_T_TIME);
00304         iec_set_value(*et, iec_timer(*timer)->et);
00305     }
00306 
00307     return IEC_OK;
00308 }
