00112 uint8_t iec_sr(iec_t *q1, iec_t s1, iec_t r) {
00113     iec_type_allowed(s1, IEC_T_BOOL);
00114     iec_type_allowed(r, IEC_T_BOOL);
00115 
00116     iec_totype(q1, IEC_T_BOOL);
00117     iec_set_maintain(*q1);
00118 
00119     iec_set_value(*q1, (iec_get_value(s1) || (iec_get_value((*q1)) && !(iec_get_value(r)))));
00120 
00121     return IEC_OK;
00122 }
