00135     iec_type_allowed(r1, IEC_T_BOOL);
00136 
00137     iec_totype(q1, IEC_T_BOOL);
00138     iec_set_maintain(*q1);
00139 
00140     iec_set_value(*q1, ((iec_get_value((*q1)) || iec_get_value(s)) && !(iec_get_value(r1))));
00141 
00142     return IEC_OK;
00143 }
