00153 uint8_t iec_r_trig(iec_t *q, iec_t clk) {
00154     iec_type_allowed(clk, IEC_T_BOOL);
00155 
00156     iec_totype(q, IEC_T_BOOL);
00157     iec_set_maintain(*q);
00158 
00159     iec_set_value(*q, (iec_get_value(clk) && !(iec_is_flag1(*q))));
00160     if (iec_get_value(clk))
00161         iec_set_flag1(*q);
00162     else
00163         iec_unset_flag1(*q);
00164 
00165     return IEC_OK;
00166 }
