00176 uint8_t iec_f_trig(iec_t *q, iec_t clk) {
00177     iec_type_allowed(clk, IEC_T_BOOL);
00178 
00179     iec_totype(q, IEC_T_BOOL);
00180     iec_set_maintain(*q);
00181 
00182     iec_set_value(*q, (!iec_get_value(clk) && !(iec_is_flag1(*q))));
00183     if (iec_get_value(clk))
00184         iec_unset_flag1(*q);
00185     else
00186         iec_set_flag1(*q);
00187 
00188     return IEC_OK;
00189 }
