00202 uint8_t iec_ctu(iec_t *q, iec_t cu, iec_t r, iec_t pv, iec_t *cv) {
00203     iec_type_allowed(cu, IEC_T_R_EDGE);
00204     iec_type_allowed(r, IEC_T_BOOL);
00205     iec_type_allowed(pv, IEC_T_INT);
00206     iec_type_allowed(*cv, IEC_T_INT);
00207 
00208     iec_totype(q, IEC_T_BOOL);
00209 
00210     if (iec_get_value(r)) {
00211         iec_set_value(*cv, 0);
00212     } else if (iec_get_value(cu) && (iec_get_value(*cv) < iec_get_value(pv)))
00213         iec_set_value(*cv, iec_get_value(*cv) + 1);
00214 
00215     iec_set_value(*q, iec_get_value(*cv) >= iec_get_value(pv))
00216 
00217     return IEC_OK;
00218 }
