00231 uint8_t iec_ctd(iec_t *q, iec_t cd, iec_t ld, iec_t pv, iec_t *cv) {
00232     iec_type_allowed(cd, IEC_T_R_EDGE);
00233     iec_type_allowed(ld, IEC_T_BOOL);
00234     iec_type_allowed(pv, IEC_T_INT);
00235     iec_type_allowed(*cv, IEC_T_INT);
00236 
00237     iec_totype(q, IEC_T_BOOL);
00238 
00239     if (iec_get_value(ld)) {
00240         iec_set_value(*cv, iec_get_value(pv));
00241     } else if (iec_get_value(cd) && (iec_get_value(*cv) > 0))
00242         iec_set_value(*cv, iec_get_value(*cv) - 1);
00243 
00244     iec_set_value(*q, iec_get_value(*cv) <= 0)
00245 
00246     return IEC_OK;
00247 }
