00257 uint8_t iec_initialize_timer(iec_t *timer, iec_t pt) {
00258     iec_type_allowed(*timer, IEC_T_TIMER);
00259     iec_type_allowed(pt, IEC_T_TIME);
00260 
00261     iec_timer(*timer)->q = false;
00262     iec_timer(*timer)->pt = iec_get_value(pt);
00263     iec_timer(*timer)->et = 0;
00264     iec_timer(*timer)->timer_run = false;
00265     iec_timer(*timer)->t0 = 0;
00266 
00267     iec_set_initialized(*timer);
00268 
00269     return IEC_OK;
00270 }
