00282 uint8_t iec_tp(iec_t *timer, iec_t in, iec_t pt, iec_t *et) {
00283     iec_type_allowed(*timer, IEC_T_TIMER);
00284 
00285     if (!iec_is_initialized(*timer))
00286         iec_initialize_timer(timer, pt);
00287 
00288     if (iec_get_value(in)) {
00289         if (iec_timer(*timer)->t0 == 0) {
00290             iec_timer(*timer)->t0 = hw_millis();
00291         }
00292     }
00293 
00294     if (iec_timer(*timer)->t0 > 0) {
00295         iec_timer(*timer)->et = hw_millis() - iec_timer(*timer)->t0;
00296         if (iec_timer(*timer)->et >= iec_timer(*timer)->pt) {
00297             iec_timer(*timer)->et = iec_timer(*timer)->pt;
00298         }
00299     }
00300 
00301     if (!iec_get_value(in) && iec_timer(*timer)->et == iec_timer(*timer)->pt) {
00302         iec_timer(*timer)->t0 = 0;
00303         iec_timer(*timer)->et = 0;
00304     }
00305 
00306     iec_timer(*timer)->q = iec_timer(*timer)->et > 0 && iec_timer(*timer)->et < iec_timer(*timer)->pt;
00307 
00308     if (*et != NULL) {
00309         iec_type_allowed(*et, IEC_T_TIME);
00310         iec_set_value(*et, iec_timer(*timer)->et);
00311     }
00312 
00313     return IEC_OK;
00314 }
