00326 uint8_t iec_ton(iec_t *timer, iec_t in, iec_t pt, iec_t *et) {
00327     iec_type_allowed(*timer, IEC_T_TIMER);
00328 
00329     if (!iec_is_initialized(*timer))
00330         iec_initialize_timer(timer, pt);
00331 
00332     if (!iec_get_value(in)) {
00333         iec_timer(*timer)->et = 0;
00334         iec_timer(*timer)->t0 = 0;
00335         iec_timer(*timer)->q = false;
00336     } else {
00337         if (iec_timer(*timer)->t0 == 0) {
00338             iec_timer(*timer)->t0 = hw_millis();
00339         }
00340         iec_timer(*timer)->et = hw_millis() - iec_timer(*timer)->t0;
00341         if (iec_timer(*timer)->et >= iec_timer(*timer)->pt) {
00342             iec_timer(*timer)->et = iec_timer(*timer)->pt;
00343             iec_timer(*timer)->q = true;
00344         }
00345     }
00346 
00347     if (*et != NULL) {
00348         iec_type_allowed(*et, IEC_T_TIME);
00349         iec_set_value(*et, iec_timer(*timer)->et);
00350     }
00351 
00352     return IEC_OK;
00353 }
